import "./Nav.css";
import { useDispatch } from "react-redux";
import { isModalClickedActions } from "../store";

const Modal = () => {
  const dispatch = useDispatch();

  const SocialKakao = () => {
    const Rest_api_key = "0dfb415be44f437c8e5d636dfa5d1c4c"; //REST API KEY
    const redirect_uri = "http://localhost:3000/"; //Redirect URI
    // oauth 요청 URL
    const kakaoURL = `https://kauth.kakao.com/oauth/authorize?client_id=${Rest_api_key}&redirect_uri=${redirect_uri}&response_type=code`;
    const handleLogin = () => {
      window.location.href = kakaoURL;
    };
    return (
      <>
        <button onClick={handleLogin}>카카오 로그인</button>
      </>
    );
  };

  function signUpKakao() {
    Kakao.Auth.authorize({
      redirectUri: "http://127.0.0.1:5500/index.html",
      state: "userme",
    });
  }
  return (
    <section class="modal">
      <div class="modal-container flex justify-center align-center">
        <div class="modal-content flex-col align-center">
          <img
            src="https://m.bunjang.co.kr/pc-static/resource/56db3dd43075482b1d31.png"
            alt=""
            width="30"
            height="34"
          />
          <button
            class="close-btn"
            onClick={() => dispatch(isModalClickedActions.toggle())}
          >
            <img
              src="https://m.bunjang.co.kr/pc-static/resource/ee442d3dd827628bc5fe.png"
              alt=""
              width="24px"
              height="24px"
            />
          </button>
          <div>번개장터로 중고거래 시작하기</div>
          <div>간편하게 가입하고 상품을 확인하세요</div>
          <div class="flex-col justify-center align-center">
            <button
              class="sign-up sign-kakao"
              onClick={() => {
                signUpKakao();
              }}
            >
              카카오로 이용하기
            </button>
            <button class="sign-up sign-facebook">페이스북으로 이용하기</button>
            <button class="sign-up sign-naver">네이버로 이용하기</button>
            <button class="sign-up sign-PA">본인인증으로 이용하기</button>
          </div>
          <div class="sign-info">
            <p>
              도움이 필요하시면 <strong>이메일</strong> 또는 고객센터
              <strong>1670-2901</strong>로 문의 부탁드립니다.
              <br />
              고객센터 운영시간:09~18시 (점심시간 12~13시, 주말/공휴일 제외)
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Modal;
