import { useState, useEffect, useContext } from "react";
import Product from "./Product";
import ProductDetail from "./ProductDetail";
import { useSelector, useDispatch } from "react-redux";
import { urlTotalArrayActions } from "../store";

const ProductRecommend = ({ handleProductClicked, handleUrlTotalArray }) => {
  const urlArray = useSelector((state) => state.urlTempArray.list);
  const isPageFoot = useSelector((state) => state.isPageFoot.value);
  const dispatch = useDispatch();

  const [imgArray, setImgArray] = useState([]);
  useEffect(() => {
    setImgArray([...urlArray]);
    handleUrlTotalArray(imgArray);
  }, []);

  useEffect(() => {
    if (isPageFoot) {
      setImgArray([...imgArray, ...urlArray]);
      handleUrlTotalArray(imgArray);
    }
  }, [isPageFoot]);

  return (
    <div className="ProductRecommend">
      {imgArray &&
        imgArray.map((it) => {
          return (
            <Product
              url={it.url}
              id={it.id}
              handleProductClicked={handleProductClicked}
            />
          );
        })}
    </div>
  );
};

export default ProductRecommend;
