import { useContext } from "react";
import { useNavigate, useParams } from "react-router-dom";

import { AppContext } from "../App";

const ProductDetail = ({ handleProductClicked, urlTotalArray }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  // const urlArray = useContext(AppContext);
  // console.log(urlTotalArray);
  // const clickedProduct = urlArray[id];
  const clickedProduct = urlTotalArray.filter((it) => {
    console.log(`it.id = ${it.id}`);
    console.log(`page's id is ${id}`);
    return it.id == id;
  });

  console.log(clickedProduct);
  return (
    <div>
      상품 상세 페이지
      <button
        onClick={() => {
          handleProductClicked();
          navigate(-1);
        }}
      >
        뒤로가기
      </button>
      <img src={clickedProduct.src}></img>
    </div>
  );
};

export default ProductDetail;
