    } else {
      setIsPageFoot(false);
    }
  };

  return (
    <div className="ProductRecommend">
      {isPageFoot &&
        urlArray.map((it) => {
          <Product url={it} />;
        })}
    </div>
  );
};

function makeRandURL() {
  let randNum = Math.floor(Math.random() * 150) + 1;
  return baseURL + randNum + ".png";
