import "./Nav.css";
import { useDispatch } from "react-redux";
import { isModalClickedActions } from "../store";
import { useEffect } from "react";
import axios from "axios";

const Modal = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    window.Kakao.init("3eba34b903142fe3966819d3854d6b11");
  }, []);

  const aaa = async () => {
    await window.Kakao.Auth.authorize({
      redirectUri: "http://localhost:3000/",
      state: "userme",
    }).then(handleLogin());
  };

  let searchParams = new URLSearchParams(window.location.search);
  console.log(searchParams.get("code"));

  const handleLogin = async () => {
    console.log(searchParams.get("code"));
    await axios
      .post("https://kauth.kakao.com/oauth/token", {
        data: {
          grant_type: "authorization_code",
          client_id: "0d653a32b09aca7c870400f582b4dfa7",
          redirect_uri: "http://localhost:3000/",
          code: searchParams.get("code"),
        },
        contentType: "application/x-www-form-urlencoded;charset=utf-8",
        dataType: null,
        success: function (response) {
          window.Kakao.Auth.setAccessToken(response.access_token);
          document.querySelector("button.api-btn").style.visibility = "visible";
        },
        error: function (jqXHR, error) {},
      })
      .then(() => {
        window.Kakao.API.request({
          url: "/v2/user/me",
        })
          .then(function (res) {
            alert(JSON.stringify(res));
          })
          .catch(function (err) {
            alert("failed to request user information: " + JSON.stringify(err));
          });
      });
  };

  return (
    <section class="modal">
      <div class="modal-container flex justify-center align-center">
        <div class="modal-content flex-col align-center">
          <img
            src="https://m.bunjang.co.kr/pc-static/resource/56db3dd43075482b1d31.png"
            alt=""
            width="30"
            height="34"
          />
          <button
            class="close-btn"
            onClick={() => dispatch(isModalClickedActions.toggle())}
          >
            <img
              src="https://m.bunjang.co.kr/pc-static/resource/ee442d3dd827628bc5fe.png"
              alt=""
              width="24px"
              height="24px"
            />
          </button>
          <div>번개장터로 중고거래 시작하기</div>
          <div>간편하게 가입하고 상품을 확인하세요</div>
          <div class="flex-col justify-center align-center">
            <button
              class="sign-up sign-kakao"
              onClick={() => {
                aaa();
              }}
            >
              카카오로 이용하기
            </button>
            <button class="sign-up sign-facebook">페이스북으로 이용하기</button>
            <button class="sign-up sign-naver">네이버로 이용하기</button>
            <button class="sign-up sign-PA">본인인증으로 이용하기</button>
          </div>
          <div class="sign-info">
            <p>
              도움이 필요하시면 <strong>이메일</strong> 또는 고객센터
              <strong>1670-2901</strong>로 문의 부탁드립니다.
              <br />
              고객센터 운영시간:09~18시 (점심시간 12~13시, 주말/공휴일 제외)
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Modal;
